function agencias_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('agencias');
	if(!$arrPerm['insert']) {
		$error_message = $Translation['no insert permission'];
		return false;
	}

	$data = [
		'agencia_id' => Request::val('agencia_id', ''),
		'nombre' => Request::val('nombre', ''),
		'direccion' => Request::val('direccion', ''),
		'codigo_postal' => Request::val('codigo_postal', ''),
		'localidad' => Request::val('localidad', ''),
		'provincia' => Request::val('provincia', ''),
		'telefono' => Request::val('telefono', ''),
		'email' => Request::val('email', ''),
		'responsable_id' => Request::val('responsable_id', ''),
		'fecha_alta' => Request::dateComponents('fecha_alta', 'curdate()'),
		'activo' => Request::val('activo', '1'),
	];

	// record owner is current user
	$recordOwner = getLoggedMemberID();

	$recID = tableInsert('agencias', $data, $recordOwner, $error_message);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID')) && $recID !== false)
		agencias_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function agencias_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);
	$currentUsername = getLoggedMemberID();
	$errorMessage = '';

	// launch requests, asynchronously
	curl_batch($requests);
}

function agencias_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('agencias', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: agencias_before_delete
	if(function_exists('agencias_before_delete')) {
		$args = [];
		if(!agencias_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: ''
			);
	}

	sql("DELETE FROM `agencias` WHERE `agencia_id`='{$selected_id}'", $eo);

	// hook: agencias_after_delete
	if(function_exists('agencias_after_delete')) {
		$args = [];
		agencias_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='agencias' AND `pkValue`='{$selected_id}'", $eo);
}

function agencias_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('agencias', $selected_id, 'edit')) return false;

	$data = [
		'agencia_id' => Request::val('agencia_id', ''),
		'nombre' => Request::val('nombre', ''),
		'direccion' => Request::val('direccion', ''),
		'codigo_postal' => Request::val('codigo_postal', ''),
		'localidad' => Request::val('localidad', ''),
		'provincia' => Request::val('provincia', ''),
		'telefono' => Request::val('telefono', ''),
		'email' => Request::val('email', ''),
		'responsable_id' => Request::val('responsable_id', ''),
		'fecha_alta' => Request::dateComponents('fecha_alta', ''),
		'activo' => Request::val('activo', ''),
	];

	if($data['agencia_id'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Agencia id': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['nombre'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Nombre': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('agencias', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: agencias_before_update
	if(function_exists('agencias_before_update')) {
		$args = ['old_data' => $old_data];
		if(!agencias_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'agencias',
		backtick_keys_once($set),
		['`agencia_id`' => $selected_id],
		$error_message
	)) {
		echo $error_message;
		echo '<a href="agencias_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}

	$data['selectedID'] = $data['agencia_id'];
	$newID = $data['agencia_id'];

	update_calc_fields('agencias', $data['selectedID'], calculated_fields()['agencias']);

	// hook: agencias_after_update
	if(function_exists('agencias_after_update')) {
		if($row = getRecord('agencias', $data['selectedID'])) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['agencia_id'];
		$args = ['old_data' => $old_data];
		if(!agencias_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('agencias', $selected_id);

	// if PK value changed, update ownership and $selected_id
	if($selected_id != $newID) {
		update('membership_userrecords', ['pkValue' => $newID], ['pkValue' => $selected_id, 'tableName' => 'agencias']);
		$selected_id = $newID;
	}
}

