function encargos_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('encargos');
	if(!$arrPerm['insert']) {
		$error_message = $Translation['no insert permission'];
		return false;
	}

	$data = [
		'encargo_id' => Request::val('encargo_id', ''),
		'referencia' => Request::val('referencia', ''),
		'inmueble_id' => Request::val('inmueble_id', ''),
		'propietario_id' => Request::val('propietario_id', ''),
		'estado_id' => Request::val('estado_id', ''),
		'estado_contacto_id' => Request::val('estado_contacto_id', ''),
		'motivo' => Request::val('motivo', ''),
		'tipo_procedencia_id' => Request::val('tipo_procedencia_id', ''),
		'precio' => Request::val('precio', ''),
		'precio_parking_incluido' => Request::val('precio_parking_incluido', '0'),
		'valoracion' => Request::val('valoracion', ''),
		'valoracion_solo_inmueble' => Request::val('valoracion_solo_inmueble', ''),
		'nuda_propiedad' => Request::val('nuda_propiedad', '0'),
		'tratabilidad' => Request::val('tratabilidad', ''),
		'fecha_creacion' => parseCode('<%%creationDateTime%%>curdate()', true, true),
		'fecha_cierre' => Request::dateComponents('fecha_cierre', ''),
		'motivo_cierre_id' => Request::val('motivo_cierre_id', ''),
		'fecha_ultima_cita' => Request::dateComponents('fecha_ultima_cita', ''),
		'fecha_ultima_actividad' => Request::dateComponents('fecha_ultima_actividad', ''),
		'fecha_ultimo_contacto' => Request::dateComponents('fecha_ultimo_contacto', ''),
		'nota_privada' => br2nl(Request::val('nota_privada', '')),
		'llaves_oficina' => Request::val('llaves_oficina', '0'),
	];

	// record owner is current user
	$recordOwner = getLoggedMemberID();

	$recID = tableInsert('encargos', $data, $recordOwner, $error_message);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID')) && $recID !== false)
		encargos_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function encargos_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);
	$currentUsername = getLoggedMemberID();
	$errorMessage = '';

	// launch requests, asynchronously
	curl_batch($requests);
}

function encargos_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('encargos', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: encargos_before_delete
	if(function_exists('encargos_before_delete')) {
		$args = [];
		if(!encargos_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: ''
			);
	}

	sql("DELETE FROM `encargos` WHERE `encargo_id`='{$selected_id}'", $eo);

	// hook: encargos_after_delete
	if(function_exists('encargos_after_delete')) {
		$args = [];
		encargos_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='encargos' AND `pkValue`='{$selected_id}'", $eo);
}

