		'valoracion_solo_inmueble' => Request::val('valoracion_solo_inmueble', ''),
		'nuda_propiedad' => Request::val('nuda_propiedad', ''),
		'tratabilidad' => Request::val('tratabilidad', ''),
		'fecha_modificacion' => parseCode('<%%editingDateTime%%>current_timestamp()', false, true),
		'fecha_cierre' => Request::dateComponents('fecha_cierre', ''),
		'motivo_cierre_id' => Request::val('motivo_cierre_id', ''),
		'fecha_ultima_cita' => Request::dateComponents('fecha_ultima_cita', ''),
		'fecha_ultima_actividad' => Request::dateComponents('fecha_ultima_actividad', ''),
		'fecha_ultimo_contacto' => Request::dateComponents('fecha_ultimo_contacto', ''),
		'nota_privada' => br2nl(Request::val('nota_privada', '')),
		'llaves_oficina' => Request::val('llaves_oficina', ''),
	];

	if($data['encargo_id'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Encargo id': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['inmueble_id'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Inmueble id': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['propietario_id'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Propietario id': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['estado_id'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Estado id': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['motivo'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Motivo': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	if($data['precio'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Precio': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('encargos', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: encargos_before_update
	if(function_exists('encargos_before_update')) {
		$args = ['old_data' => $old_data];
		if(!encargos_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'encargos',
		backtick_keys_once($set),
		['`encargo_id`' => $selected_id],
		$error_message
	)) {
		echo $error_message;
		echo '<a href="encargos_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}

	$data['selectedID'] = $data['encargo_id'];
	$newID = $data['encargo_id'];

	update_calc_fields('encargos', $data['selectedID'], calculated_fields()['encargos']);

	// hook: encargos_after_update
	if(function_exists('encargos_after_update')) {
		if($row = getRecord('encargos', $data['selectedID'])) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['encargo_id'];
		$args = ['old_data' => $old_data];
		if(!encargos_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('encargos', $selected_id);

	// if PK value changed, update ownership and $selected_id
	if($selected_id != $newID) {
		update('membership_userrecords', ['pkValue' => $newID], ['pkValue' => $selected_id, 'tableName' => 'encargos']);
		$selected_id = $newID;
	}
}

