		'm2_construidos' => Request::val('m2_construidos', ''),
		'm2_terraza' => Request::val('m2_terraza', ''),
		'altura' => Request::val('altura', ''),
		'orientacion' => Request::val('orientacion', ''),
		'estado_conservacion' => Request::val('estado_conservacion', ''),
		'ano_construccion' => Request::val('ano_construccion', ''),
		'ano_reforma' => Request::val('ano_reforma', ''),
		'ascensor' => Request::val('ascensor', '0'),
		'terraza' => Request::val('terraza', '0'),
		'balcon' => Request::val('balcon', '0'),
		'garaje' => Request::val('garaje', '0'),
		'trastero' => Request::val('trastero', '0'),
		'calefaccion_tipo' => Request::val('calefaccion_tipo', ''),
		'climatizacion' => Request::val('climatizacion', ''),
		'certificado_energetico' => Request::val('certificado_energetico', ''),
		'consumo_energetico' => Request::val('consumo_energetico', ''),
		'emisiones_co2' => Request::val('emisiones_co2', ''),
		'referencia_catastral' => Request::val('referencia_catastral', ''),
		'fecha_alta' => parseCode('<%%creationDateTime%%>curdate()', true, true),
		'llaves_oficina' => Request::val('llaves_oficina', '0'),
		'notas_privadas' => br2nl(Request::val('notas_privadas', '')),
		'observaciones' => br2nl(Request::val('observaciones', '')),
	];

	// record owner is current user
	$recordOwner = getLoggedMemberID();

	$recID = tableInsert('inmuebles', $data, $recordOwner, $error_message);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID')) && $recID !== false)
		inmuebles_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function inmuebles_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);
	$currentUsername = getLoggedMemberID();
	$errorMessage = '';

	// launch requests, asynchronously
	curl_batch($requests);
}

function inmuebles_delete($selected_id, $AllowDeleteOfParents = false, $skipChecks = false) {
	// insure referential integrity ...
	global $Translation;
	$selected_id = makeSafe($selected_id);

	// mm: can member delete record?
	if(!check_record_permission('inmuebles', $selected_id, 'delete')) {
		return $Translation['You don\'t have enough permissions to delete this record'];
	}

	// hook: inmuebles_before_delete
	if(function_exists('inmuebles_before_delete')) {
		$args = [];
		if(!inmuebles_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'] . (
				!empty($args['error_message']) ?
					'<div class="text-bold">' . strip_tags($args['error_message']) . '</div>'
					: ''
			);
	}

	sql("DELETE FROM `inmuebles` WHERE `inmueble_id`='{$selected_id}'", $eo);

	// hook: inmuebles_after_delete
	if(function_exists('inmuebles_after_delete')) {
		$args = [];
		inmuebles_after_delete($selected_id, getMemberInfo(), $args);
	}

	// mm: delete ownership data
	sql("DELETE FROM `membership_userrecords` WHERE `tableName`='inmuebles' AND `pkValue`='{$selected_id}'", $eo);
}

function inmuebles_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('inmuebles', $selected_id, 'edit')) return false;

	$data = [
		'inmueble_id' => Request::val('inmueble_id', ''),
		'referencia' => Request::val('referencia', ''),
		'propietario_id' => Request::val('propietario_id', ''),
		'estado' => Request::val('estado', ''),
		'tipologia_id' => Request::val('tipologia_id', ''),
		'subtipologia_id' => Request::val('subtipologia_id', ''),
		'direccion_id' => Request::val('direccion_id', ''),
		'zona_id' => Request::val('zona_id', ''),
		'dormitorios' => Request::val('dormitorios', ''),
		'banos' => Request::val('banos', ''),
		'aseos' => Request::val('aseos', ''),
		'm2_utiles' => Request::val('m2_utiles', ''),
		'm2_construidos' => Request::val('m2_construidos', ''),
		'm2_terraza' => Request::val('m2_terraza', ''),
		'altura' => Request::val('altura', ''),
		'orientacion' => Request::val('orientacion', ''),
		'estado_conservacion' => Request::val('estado_conservacion', ''),
		'ano_construccion' => Request::val('ano_construccion', ''),
		'ano_reforma' => Request::val('ano_reforma', ''),
		'ascensor' => Request::val('ascensor', ''),
		'terraza' => Request::val('terraza', ''),
		'balcon' => Request::val('balcon', ''),
		'garaje' => Request::val('garaje', ''),
		'trastero' => Request::val('trastero', ''),
		'calefaccion_tipo' => Request::val('calefaccion_tipo', ''),
		'climatizacion' => Request::val('climatizacion', ''),
		'certificado_energetico' => Request::val('certificado_energetico', ''),
		'consumo_energetico' => Request::val('consumo_energetico', ''),
		'emisiones_co2' => Request::val('emisiones_co2', ''),
		'referencia_catastral' => Request::val('referencia_catastral', ''),
		'ultima_modificacion' => parseCode('<%%editingDateTime%%>current_timestamp()', false, true),
		'llaves_oficina' => Request::val('llaves_oficina', ''),
		'notas_privadas' => br2nl(Request::val('notas_privadas', '')),
		'observaciones' => br2nl(Request::val('observaciones', '')),
	];

	if($data['inmueble_id'] === '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">{$Translation['error:']} 'Inmueble id': {$Translation['field not null']}<br><br>";
		echo '<a href="" onclick="history.go(-1); return false;">' . $Translation['< back'] . '</a></div>';
		exit;
	}
	// get existing values
	$old_data = getRecord('inmuebles', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: inmuebles_before_update
	if(function_exists('inmuebles_before_update')) {
		$args = ['old_data' => $old_data];
		if(!inmuebles_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'inmuebles',
		backtick_keys_once($set),
		['`inmueble_id`' => $selected_id],
		$error_message
	)) {
		echo $error_message;
		echo '<a href="inmuebles_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}

	$data['selectedID'] = $data['inmueble_id'];
	$newID = $data['inmueble_id'];

	update_calc_fields('inmuebles', $data['selectedID'], calculated_fields()['inmuebles']);

	// hook: inmuebles_after_update
	if(function_exists('inmuebles_after_update')) {
		if($row = getRecord('inmuebles', $data['selectedID'])) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['inmueble_id'];
		$args = ['old_data' => $old_data];
		if(!inmuebles_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('inmuebles', $selected_id);

	// if PK value changed, update ownership and $selected_id
	if($selected_id != $newID) {
		update('membership_userrecords', ['pkValue' => $newID], ['pkValue' => $selected_id, 'tableName' => 'inmuebles']);
		$selected_id = $newID;
	}
}

function inmuebles_form($selectedId = '', $allowUpdate = true, $allowInsert = true, $allowDelete = true, $separateDV = true, $templateDV = '', $templateDVP = '') {
